import React, { useState } from 'react';
import { Button } from '@mui/material';
import BasicModal from './BasicModal';

export default function Jobtitle({ jobs, searchItem, user, setUser }) {
  const [modalOpen, setModalOpen] = useState(false);

  const handleLearnMoreClick = () => {
    if (!user) {
      setModalOpen(true);  // Mở modal đăng nhập khi người dùng chưa đăng nhập
    } else {
      // Điều hướng đến trang chi tiết công việc
      console.log('Navigate to job details');
    }
  };

  return (
    <div>
      {/* Hiển thị modal đăng nhập nếu cần */}
      <BasicModal open={modalOpen} setOpen={setModalOpen} setUser={setUser} />

      {jobs
        .filter(job => job.title.toLowerCase().includes(searchItem.toLowerCase()))
        .map(job => (
          <div key={job.id}>
            <h2>{job.title}</h2>
            <Button onClick={handleLearnMoreClick}>Learn More</Button>
          </div>
        ))}
    </div>
  );
}
