import React, { useState } from "react";
import Header from "./component/Header";
import { Box, Pagination, ThemeProvider, createTheme } from "@mui/material";
import './App.css';
import Jobtitle from "./component/Jobtitle";

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1976d2',
    },
  },
});

function App() {
  const [page, setPage] = useState(1);

  // Hàm xử lý sự kiện khi người dùng thay đổi trang
  const handlePageChange = (event, value) => {
    setPage(value);
    // Thực hiện bất kỳ hành động nào bạn cần khi trang thay đổi
    // Ví dụ: fetch dữ liệu cho trang mới
    console.log(`Đang chuyển đến trang ${value}`);
  };

  return (
    <div>
      <ThemeProvider theme={darkTheme}>
        <Header />
        <Jobtitle />
      </ThemeProvider>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          mt: 2,
        }}
      >
        <Pagination
          count={3} // Tổng số trang
          page={page} // Trang hiện tại
          onChange={handlePageChange} // Hàm xử lý sự kiện khi thay đổi trang
          sx={{
            '& .MuiPaginationItem-root': {
              color: 'white',
              borderColor: 'transparent',
            },
            '& .Mui-selected': {
              backgroundColor: 'red !important',
              color: 'white',
            },
          }}
        />
      </Box>
    </div>
  );
}

export default App;
