import React, { useState } from 'react';
import { Box, IconButton, OutlinedInput, InputLabel, InputAdornment, FormControl, TextField, Typography, Button } from '@mui/material';
import { Visibility, VisibilityOff, LockIcon } from '@mui/icons-material';
import { useAuth } from './AuthProvider'; // Import useAuth

export default function InputAdornments() {
  const [showPassword, setShowPassword] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useAuth(); // Sử dụng useAuth để lấy hàm login

  const handleClickShowPassword = () => setShowPassword((show) => !show);
  const handleMouseDownPassword = (event) => event.preventDefault();

  const handleLogin = () => {
    login(username, password); // Gọi hàm login khi nhấn nút Sign In
  };

  return (
    <div>
      <Box sx={{ display: 'flex', justifyContent: 'center', alignContent: 'center' }}>
        <LockIcon sx={{ fontSize: '30px', borderRadius: '50%', backgroundColor: 'rgb(215, 71, 66)', padding: '12px' }} />
      </Box>
      <Typography sx={{ color: '#fff', textAlign: 'center', marginTop: '5px', marginBottom: '8px', fontSize: '20px' }}>
        Log In
      </Typography>

      <Box sx={{ display: 'flex', flexDirection: 'column' }}>
        <TextField
          helperText=" "
          id="demo-helper-text-aligned-no-helper"
          label="User name"
          value={username}
          onChange={(e) => setUsername(e.target.value)} // Cập nhật state username
        />
        <FormControl variant="outlined">
          <InputLabel htmlFor="outlined-adornment-password">Password</InputLabel>
          <OutlinedInput
            id="outlined-adornment-password"
            type={showPassword ? 'text' : 'password'}
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Cập nhật state password
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            }
            label="Password"
          />
        </FormControl>
        <Button
          variant="contained"
          sx={{ marginTop: '25px', marginBottom: '25px', backgroundColor: 'rgb(215, 71, 66)' }}
          onClick={handleLogin} // Gọi hàm handleLogin khi nhấn nút
        >
          Sign In
        </Button>
      </Box>
      <Box>
        <Typography sx={{ color: 'rgb(215, 71, 66)', cursor: 'pointer', display: 'flex', justifyContent: 'space-between' }}>
          Forgot Password?
          <Typography>Don't have an account? Sign up</Typography>
        </Typography>
      </Box>
    </div>
  );
}
