import React, { useState } from "react";
import {  ThemeProvider,createTheme } from "@mui/material";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css'
import Jobtitle from "./component/Jobtitle";
import Pagenext from "./component/Pagenext";
import Header from "./component/Header";
import LoginPage from "./LoginPage";
import BasicModal from "./component/BasicModal";
import jobs from './jobs.json'


const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1976d2',
    },
  },
});

function App() {
  const [ page, setPage] = useState(1)
  const [ searchItem , setSearchItem] = useState('')

  const jobsPerPage = 5;
  const totalPages = Math.ceil(jobs.length / jobsPerPage);
  const currentJobs = jobs.slice((page - 1) * jobsPerPage, page * jobsPerPage);

  const handlePageChange = ( event,value ) => {
    console.log(event)
    setPage(value)
  }
  const handleSearchItem = (item) => {
    setSearchItem(item)
  }


  return (
    <div>
      <ThemeProvider  theme={darkTheme} >
        <Header onSearchChange={handleSearchItem}></Header>
          <Routes>
              <Route path="/" element={<Jobtitle jobs={currentJobs}  searchItem={searchItem} />}></Route>
              <Route path="/sign-in" element={<LoginPage />}></Route>
          </Routes>
            <Pagenext page={page} totalPages={totalPages} handlePageChange={handlePageChange}></Pagenext>
      </ThemeProvider>
    </div>
  );
}

export default App;
