import React from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, Divider, Chip } from '@mui/material';

function JobDetail({ jobs }) {
  const { id } = useParams();
  const job = jobs.find(job => job.id === parseInt(id)); // Tìm công việc theo id

  if (!job) return <Typography variant="h6">Job not found</Typography>;

  return (
    <Box sx={{ padding: 4, bgcolor: "#424242", color: "#fff" }}>
      <Typography variant="h3">{job.title}</Typography>
      <Divider sx={{ margin: '10px 0' }} />
      <Typography variant="h6">{job.description}</Typography>
      <Box sx={{ marginTop: 2 }}>
        <Typography variant="h6">Skills:</Typography>
        {job.skills.map((skill, index) => (
          <Chip
            label={skill}
            key={index}
            style={{
              fontSize: '0.53rem',
              height: '20px',
              backgroundColor: 'red',
              marginRight: '4px'
            }}
          />
        ))}
      </Box>
      <Typography variant="h6" sx={{ marginTop: 2 }}>City: {job.city}</Typography>
    </Box>
  );
}

export default JobDetail;
