import React, { useState } from "react";
import Header from "./component/Header"
import {   Box, Pagination, ThemeProvider,createTheme } from "@mui/material";
import './App.css'
import Jobtitle from "./component/Jobtitle";
import jobs from './jobs.json'

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1976d2',
    },
  },
});

function App() {
  const [ page, setPage] = useState(1)

  const jobsPerPage = 5;

  const totalPages = Math.ceil(jobs.length / jobsPerPage);

  const currentJobs = jobs.slice((page - 1) * jobsPerPage, page * jobsPerPage);

  const handlePageChange = ( value ) => {
    setPage(value)
  }

  return (
    <div>
      <ThemeProvider  theme={darkTheme} >
        <Header />
        <Jobtitle jobs={currentJobs}></Jobtitle>
      </ThemeProvider>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          mt: 2,
        }}
      >
        <Pagination 
        count={totalPages} 
        onChange={handlePageChange}
         sx={{
          '& .MuiPaginationItem-root': {
            color: 'white',              
            borderColor: 'transparent',        
          },
          '& .Mui-selected': {
            backgroundColor: 'red !important',    
            color: 'white',                   
          }
        }}
         />
      </Box>
    </div>
  );
}

export default App;
