import * as React from 'react';
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';
import TextField from '@mui/material/TextField';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import LockIcon from '@mui/icons-material/Lock';
import { Typography, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function LoginPage() {
  const [showPassword, setShowPassword] = React.useState(false);
  const [username, setUsername] = React.useState('eve.holt@reqres.in'); // Giá trị mặc định cho username
  const [password, setPassword] = React.useState('cityslicka'); // Giá trị mặc định cho password
  const [error, setError] = React.useState('');
  const navigate = useNavigate();

  const handleClickShowPassword = () => setShowPassword((show) => !show);

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  const handleSignIn = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('https://reqres.in/api/login', {
        email: username,
        password: password,
      });

      if (response.status === 200) {
        // Đăng nhập thành công, điều hướng về trang chủ "/"
        navigate('/');
      }
    } catch (err) {
      // Xử lý lỗi khi đăng nhập thất bại
      setError('Đăng nhập thất bại. Vui lòng kiểm tra lại thông tin đăng nhập.');
    }
  };

  return (
    <div>
      <Box sx={{ display: 'flex', justifyContent: 'center', alignContent: 'center' }}>
        <LockIcon sx={{ fontSize: '30px', borderRadius: '50%', backgroundColor: 'rgb(215, 71, 66)', padding: '12px' }} />
      </Box>
      <Typography sx={{ color: '#fff', textAlign: 'center', marginTop: '5px', marginBottom: '8px', fontSize: '20px' }}>
        Log In
      </Typography>

      {error && (
        <Typography sx={{ color: 'red', textAlign: 'center', marginBottom: '10px' }}>
          {error}
        </Typography>
      )}

      <Box component="form" onSubmit={handleSignIn} sx={{ display: 'flex', flexDirection: 'column' }}>
        <TextField
          helperText=" "
          id="username"
          label="User name"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          sx={{ marginBottom: '15px' }}
        />
        <FormControl variant="outlined">
          <InputLabel htmlFor="outlined-adornment-password">Password</InputLabel>
          <OutlinedInput
            id="outlined-adornment-password"
            type={showPassword ? 'text' : 'password'}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            }
            label="Password"
          />
        </FormControl>
        <Button type="submit" variant="contained" sx={{ marginTop: '25px', marginBottom: '25px', backgroundColor: 'rgb(215, 71, 66)' }}>
          Sign In
        </Button>
      </Box>
      <Box>
        <Typography sx={{ color: 'rgb(215, 71, 66)', cursor: 'pointer', display: 'flex', justifyContent: 'space-between' }}>
          Forgot Password ?
          <Typography>
            Don't have an account? Sign up
          </Typography>
        </Typography>
      </Box>
    </div>
  );
}
