import React, { useState } from "react";
import Header from "./component/Header";
import { Box, Pagination, ThemeProvider, createTheme } from "@mui/material";
import './App.css';
import Jobtitle from "./component/Jobtitle";
import jobs from './jobs.json'; // Giả sử dữ liệu công việc được lưu trữ ở đây

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1976d2',
    },
  },
});

function App() {
  const [page, setPage] = useState(1);
  const jobsPerPage = 5; // Số công việc trên mỗi trang

  // Tính toán tổng số trang
  const totalPages = Math.ceil(jobs.length / jobsPerPage);

  // Tính toán công việc hiển thị trên trang hiện tại
  const currentJobs = jobs.slice((page - 1) * jobsPerPage, page * jobsPerPage);

  const handlePageChange = (event, value) => {
    setPage(value); // Cập nhật trang hiện tại
  };

  return (
    <div>
      <ThemeProvider theme={darkTheme}>
        <Header />
        <Jobtitle jobs={currentJobs} /> {/* Truyền công việc hiện tại cho Jobtitle */}
      </ThemeProvider>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          mt: 2,
        }}
      >
        <Pagination
          count={totalPages} // Tổng số trang
          page={page} // Trang hiện tại
          onChange={handlePageChange} // Hàm xử lý sự thay đổi trang
          sx={{
            '& .MuiPaginationItem-root': {
              color: 'white',
              borderColor: 'transparent',
            },
            '& .Mui-selected': {
              backgroundColor: 'red !important',
              color: 'white',
            }
          }}
        />
      </Box>
    </div>
  );
}

export default App;
