import * as React from 'react';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import { Button, Chip, Typography } from '@mui/material';
import Jobtitle from './Jobtitle';

export default function BoxSystemProps({ job }) {
  return (
    <Paper
      elevation={3} // Điều chỉnh độ nổi (elevation)
      sx={{
        bgcolor: '#424242', // Màu nền của Paper
        borderRadius: 3,
        padding: 2,
        margin: '20px auto',
        maxWidth: 300, // Giới hạn chiều rộng
      }}
    >
      <Box
        height={250}
        display="flex"
        flexDirection="column"
        justifyContent="space-between"
        gap={2}
      >
        <Box>
          <Typography
            textAlign="center"
            color="#fff"
          >
            {job.title}
          </Typography>
          <Box
            margin='8px 0'
            display="flex"
            flexWrap="wrap"
            gap={1}
          >
            {job.skills.map((skill, index) => (
              <Chip
                key={index}
                label={skill}
                sx={{
                  backgroundColor: '#ff8f00',
                  color: '#fff',
                  fontSize: '0.75rem',
                  height: '24px',
                }}
              />
            ))}
          </Box>
          <Typography
            color="#fff"
          >
            {job.description}
          </Typography>
        </Box>
        <Button
          variant="contained"
          sx={{
            backgroundColor: '#ff8f00',
            '&:hover': {
              backgroundColor: '#ff8f00',
            },
            alignSelf: 'center',
          }}
        >
          <Typography
            style={{
              color: '#000',
              fontSize: '0.8rem',
              fontWeight: '550',
            }}
          >
            Learn More
          </Typography>
        </Button>
      </Box>
    </Paper>
  );
}
