import * as React from 'react';
import Box from '@mui/material/Box';
import {  Button, Chip, Divider, Typography } from '@mui/material';
import Paper from '@mui/material/Paper';

export default function BoxSystemProps({ job }) {
  return (
    <Paper
    elevation={3} // Điều chỉnh độ nổi (elevation)
    sx={{
      bgcolor: '#424242', // Màu nền của Paper
      borderRadius: 3,
      padding: 2,
      margin: '20px auto',
      maxWidth: 300, // Giới hạn chiều rộng
    }}
  >
    <Box
      bgcolor="#424242"
      height={250}
      width={300}
      my={5}
      display="flex"
      flexDirection="column"
      justifyContent="space-between"
      gap={2}
      p={2}
      marginLeft={5}
      borderRadius={3}
    >
    <Box>
      <Typography
        textAlign="center"
        color="#fff"
      >
        {job.title}
      </Typography>
      <Divider 
        style={{
            margin:'5px 0 8px 0'
        }}>
      </Divider>
      <Box
          margin='8px 0 8px 0'
          display='flex'
          gap={1}
      >
          {job.skills.map((skill,index) => (
            <Chip 
            label={skill} key={index}
            style={{
              fontSize: '0.53rem', 
              height:'20px',
              backgroundColor: 'red'
            }}
             />
          ))}
        </Box>
      <Typography
        color="#fff"
        width='320px'
      >
        {job.description}
      </Typography>
    </Box>
    <Button
      variant="contained"
      sx={{
          backgroundColor: '#ff8f00',
          '&:hover': {
            backgroundColor: '#ff8f00', 
          },
          alignSelf: 'center',
          
        }}
    >
      <Typography
      style={{
        color: '#000',
        fontSize: '0.8rem',
        fontWeight: '550'
      }}
      >
        learn More
      </Typography>
    </Button>
  </Box>
  </Paper>
  );
}
