import React, { useState } from "react";
import Header from "./component/Header"
import {  ThemeProvider,createTheme } from "@mui/material";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css'
import Jobtitle from "./component/Jobtitle";
import Pagination from "./component/Pagination";
import jobs from './jobs.json'
import { AuthProvider } from './AuthProvider'

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1976d2',
    },
  },
});

function App() {
  const [ page, setPage] = useState(1)

    const jobsPerPage = 5;

    const totalPages = Math.ceil(jobs.length / jobsPerPage);

    const currentJobs = jobs.slice((page - 1) * jobsPerPage, page * jobsPerPage);

    const handlePageChange = ( event,value ) => {
      console.log(event)
      setPage(value)

  return (
    <div>
    <Router>
      <AuthProvider>
        <ThemeProvider  theme={darkTheme} >
          <Header />
          <Jobtitle jobs={currentJobs} />
        </ThemeProvider>
        <Pagination page={page} totalPages={totalPages} handlePageChange={handlePageChange}></Pagination>
      </AuthProvider>
    </Router>
    </div>
  );
}
}

export default App;
