import React, { useState } from "react";
import Header from "./component/Header";
import { ThemeProvider, createTheme } from "@mui/material";
import { BrowserRouter as Router } from 'react-router-dom';
import './App.css';
import Jobtitle from "./component/Jobtitle";
import jobs from './jobs.json';
import Pagenext from "./component/Pagenext";

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1976d2',
    },
  },
});

function App() {
  const [page, setPage] = useState(1);
  const [searchItem, setSearchItem] = useState('');

  const jobsPerPage = 5;

  // Filter jobs based on search item
  const filteredJobs = jobs.filter(job =>
    job.title.toLowerCase().includes(searchItem.toLowerCase())
  );

  const totalPages = Math.ceil(filteredJobs.length / jobsPerPage);
  const currentJobs = filteredJobs.slice((page - 1) * jobsPerPage, page * jobsPerPage);

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  const handleSearchChange = (value) => {
    setSearchItem(value);
    // Calculate the new page number based on search results
    // Adjust page if it exceeds the total number of pages after filtering
    const newTotalPages = Math.ceil(filteredJobs.length / jobsPerPage);
    if (page > newTotalPages) {
      setPage(newTotalPages);
    }
  };

  return (
    <div>
      <Router>
        <ThemeProvider theme={darkTheme}>
          <Header onSearchChange={handleSearchChange} />
          <Jobtitle jobs={currentJobs} />
          <Pagenext page={page} totalPages={totalPages} handlePageChange={handlePageChange} />
        </ThemeProvider>
      </Router>
    </div>
  );
}

export default App;
